use strict;
use warnings;
use utf8;

## nofilter(TidyAll::Plugin::OTRS::Perl::Dumper)

# A ../ használata a lib helyeként
use File::Basename;
use FindBin qw($RealBin);
use lib dirname($RealBin);

use JSON;
use REST::Client;

# Ez a HOST a webszolgáltatásnál. A formátum:
# <HTTP_TYPE>:://<OTRS_FQDN>/nph-genericinterface.pl
my $Host = 'http://localhost/otrs/nph-genericinterface.pl';

my $RestClient = REST::Client->new(
    {
        host => $Host,
    }
);

# Ezek a vezérlők és a szolgáltatók. A formátum:
# /Webservice/<WEBSZOLGÁLTATÁS_NEVE/<ERŐFORRÁS>/<KÉRÉS_ÉRTÉK>
# vagy
# /WebserviceID/<WEBSZOLGÁLTATÁS_AZONOSÍTÓJA>/<ERŐFORRÁS>/<KÉRÉS_ÉRTÉK>
#
# Nézze meg a dokumentációt a szolgáltatók beállításához.
#
# Ez a példa le fogja kérni a TicketID = 1 azonosítóval rendelkező jegyet.
(<KÉRÉS_ÉRTÉK>)
my $GetControllerAndRequest = '/Webservice/GenericTicketConnectorREST/Ticket/1';

# Ez a példa az alap URL a jegylétrehozáshoz
my $CreateControllerAndRequest = '/Webservice/GenericTicketConnectorREST/Ticket';

# Ez a példa frissíteni fogja a TicketID = 1 azonosítóval rendelkező jegyet (<KÉRÉS_ÉRTÉK>)
my $UpdateControllerAndRequest = '/Webservice/GenericTicketConnectorREST/Ticket/1';

# Ez az alap URL a jegkereséshez
my $SearchControllerAndRequest = '/Webservice/GenericTicketConnectorREST/Ticket';

# Ez az alap URL a TicketID = 1 azonosítóval rendelkező jegy előzményéhez (<KÉRÉS_ÉRTÉK>)
my $HistoryControllerAndRequest = '/Webservice/GenericTicketConnectorREST/TicketHistory/1';

# Jegylekérés példa
# Nézze meg az OTRSGenericInterfaceREST dokumentációját a beállításhoz
#   - webszolgáltatás
#   - átvitel
#   - műveletek
my $GetParams = {
    # érvényes ügyintézői felhasználónévvel kell kitölteni
    UserLogin => "valamilyen ügyintéző-felhasználó bejelentkezés",
    # érvényes ügyintézői jelszóval kell kitölteni
    Password  => "valamilyen ügyintéző-felhasználó jelszó",
};

# A GetParams összeállítása az URL részeként a REST-GET kérésekhez
my $QueryParams = $RestClient->buildQuery( %{$GetParams} );
$GetControllerAndRequest .= $QueryParams;

$RestClient->GET($GetControllerAndRequest);

my $GetResponseCode = $RestClient->responseCode();

if ( $GetResponseCode ne '200' ) {
    print "Get request failed, response code was: $GetResponseCode\n";
}
else {

    # Ha a kérést helyesen válaszolták meg, itt egy JSON szöveget kapunk.
    my $ResponseContent = $RestClient->responseContent();

    my $Data = decode_json $ResponseContent;

    # Egyszerűen kiírjuk a kapott adatszerkezetet:
    use Data::Dumper;
    print "Get response was:\n";
    print Dumper($Data);

}

# Jegykeresés példa
# Nézze meg az OTRSGenericInterfaceREST dokumentációját a beállításhoz
#   - webszolgáltatás
#   - átvitel
#   - műveletek
my $SearchParams = {
    # érvényes ügyintézői felhasználónévvel kell kitölteni
    UserLogin => "valamilyen ügyintéző-felhasználó bejelentkezés",
    # érvényes ügyintézői jelszóval kell kitölteni
    Password  => "valamilyen ügyintéző-felhasználó jelszó",
    Queues    => ['Raw'],
};

# A SearchParams összeállítása az URL részeként a REST-GET kérésekhez
$QueryParams = $RestClient->buildQuery( %{$SearchParams} );
$SearchControllerAndRequest .= $QueryParams;

$RestClient->GET($SearchControllerAndRequest);

# Ha a gép nem érhető el, rosszul van beállítva vagy nem tudta kiszolgálni a kért oldalt:
my $SearchResponseCode = $RestClient->responseCode();

if ( $SearchResponseCode ne '200' ) {
    print "Search request failed, response code was: $SearchResponseCode\n";
}
else {

    # Ha a kérést helyesen válaszolták meg, itt egy JSON szöveget kapunk.
    my $ResponseContent = $RestClient->responseContent();

    my $Data = decode_json $ResponseContent;

    # Egyszerűen kiírjuk a kapott adatszerkezetet:
    use Data::Dumper;
    print "Search Response was:\n";
    print Dumper($Data);

}

# Jegylétrehozás példa
# Nézze meg az OTRSGenericInterfaceREST dokumentációját a beállításhoz
#   - webszolgáltatás
#   - átvitel
#   - műveletek
my $CreateOrUpdateParams = {
    # érvényes ügyintézői felhasználónévvel kell kitölteni
    UserLogin => "valamilyen ügyintéző-felhasználó bejelentkezés",
    # érvényes ügyintézői jelszóval kell kitölteni
    Password  => "valamilyen ügyintéző-felhasználó jelszó",
    Ticket    => {
        Title        => 'valamilyen jegycím',
        Queue        => 'Raw',
        Lock         => 'unlock',
        Type         => 'Unclassified',
        State        => 'new',
        Priority     => '3 normal',
        Owner        => 'valamilyen ügyintéző-felhasználó bejelentkezés',
        CustomerUser => 'felhasználó-1',
    },
    Article => {
        Subject     => 'valamilyen tárgy',
        Body        => 'valamilyen törzs',
        ContentType => 'text/plain; charset=utf8',
    },
};

my $CreateJSONParams = encode_json $CreateOrUpdateParams;

my @CreateRequestParam = (
    $CreateControllerAndRequest,
    $CreateJSONParams
);

# REST-POST kéréseket kell használnunk annak érdekében, hogy a UserLogin
# és a Password értékét helyesen át tudjuk küldeni, bár a másik REST
# metódusnak jobban kellene illeszkednie.
$RestClient->POST(@CreateRequestParam);

# Ha a gép nem érhető el, rosszul van beállítva vagy nem tudta kiszolgálni a kért oldalt:
my $CreateResponseCode = $RestClient->responseCode();

if ( $CreateResponseCode ne '200' ) {
    print "Create request failed, response code was: $CreateResponseCode\n";
}
else {

    # Ha a kérést helyesen válaszolták meg, itt egy JSON szöveget kapunk.
    my $ResponseContent = $RestClient->responseContent();

    my $Data = decode_json $ResponseContent;

    # Egyszerűen kiírjuk a kapott adatszerkezetet:
    use Data::Dumper;
    print "Create Response was:\n";
    print Dumper($Data);

}

# Jegyfrissítés példa
# Nézze meg az OTRSGenericInterfaceREST dokumentációját a beállításhoz
#   - webszolgáltatás
#   - átvitel
#   - műveletek
my $UpdateJSONParams = encode_json $CreateOrUpdateParams;

my @UpdateRequestParam = (
    $UpdateControllerAndRequest,
    $UpdateJSONParams
);

# REST-PATCH kéréseket kell használnunk annak érdekében, hogy a UserLogin
# és a Password értékét helyesen át tudjuk küldeni, bár a másik REST
# metódusnak jobban kellene illeszkednie.
$RestClient->PATCH(@UpdateRequestParam);

# Ha a gép nem érhető el, rosszul van beállítva vagy nem tudta kiszolgálni a kért oldalt:
my $UpdateResponseCode = $RestClient->responseCode();
if ( $UpdateResponseCode ne '200' ) {
    print "Update request failed, response code was: $UpdateResponseCode\n";
}
else {

    # Ha a kérést helyesen válaszolták meg, itt egy JSON szöveget kapunk.
    my $ResponseContent = $RestClient->responseContent();

    my $Data = decode_json $ResponseContent;

    # Egyszerűen kiírjuk a kapott adatszerkezetet:
    use Data::Dumper;
    print "Update response was:\n";
    print Dumper($Data);

}

# Jegy előzményeinek lekérése példa
# Nézze meg az OTRSGenericInterfaceREST dokumentációját a beállításhoz
#   - webszolgáltatás
#   - átvitel
#   - műveletek
my $HistoryParams = {
    # érvényes ügyintézői felhasználónévvel kell kitölteni
    UserLogin => "valamilyen ügyintéző-felhasználó bejelentkezés",
    # érvényes ügyintézői jelszóval kell kitölteni
    Password  => "valamilyen ügyintéző-felhasználó jelszó",
    TicketID  => [1],
};

# A SearchParams összeállítása az URL részeként a REST-GET kérésekhez
$QueryParams = $RestClient->buildQuery( %{$HistoryParams} );
$HistoryControllerAndRequest .= $QueryParams;

$RestClient->GET($HistoryControllerAndRequest);

# Ha a gép nem érhető el, rosszul van beállítva vagy nem tudta kiszolgálni a kért oldalt:
my $HistoryResponseCode = $RestClient->responseCode();

if ( $HistoryResponseCode ne '200' ) {
    print "History request failed, response code was: $HistoryResponseCode\n";
}
else {

    # Ha a kérést helyesen válaszolták meg, itt egy JSON szöveget kapunk.
    my $ResponseContent = $RestClient->responseContent();

    my $Data = decode_json $ResponseContent;

    # Egyszerűen kiírjuk a kapott adatszerkezetet:
    use Data::Dumper;
    print "History Response was:\n";
    print Dumper($Data);

}
